$(function() {
	
    $('.spinner_options').cycle({
        fx:     'fade',
        timeout: 2000,
        pager:  '.spinner_nav',
        continuous: false,
        pagerAnchorBuilder: PagerBuilder,
      	activePagerClass: 'activeSlide', 
		slideExpr: 'img',
		autostop: 6,
		cleartypeNoBg: true
    });
   	
    function PagerBuilder(idx, slide){
    	return '<li><a href="#"></a></li>';
    }
    
});